var sourceData200 = {"FileName":"C:\\local\\mw\\gh-isaacito12-bev\\Utility\\LiteAppAPI-v2-R2023b\\+LiteAppComponent\\LiteAppComponentBase.m","RawFileContents":["classdef LiteAppComponentBase < matlab.ui.componentcontainer.ComponentContainer\r","  %% Implementation of Lite App component\r","  % This class implements common code used by most Lite App components.\r","  % Inherit this component to implement a Lite App component.\r","  %\r","  % Documentation\r","  %\r","  % - matlab.ui.componentcontainer.ComponentContainer\r","  %   https://www.mathworks.com/help/matlab/ref/matlab.ui.componentcontainer.componentcontainer-class.html\r","\r","  % Copyright 2023-2024 The MathWorks, Inc.\r","\r","  %% Public properties\r","\r","  properties\r","\r","    CommonFontSize (1,1) {mustBeInteger, mustBePositive} = LiteAppUtility.Constant.FontSize{\"medium\"}\r","\r","    CommonPaddingTop = 2;\r","    CommonPaddingBottom = 2;\r","    CommonPaddingLeft = 4;\r","    CommonPaddingRight = 4;\r","\r","    CommonPadding = [4 2 4 2]  % left bottom right top\r","\r","    CommonColumnSpacing = 4\r","    CommonRowSpacing = 0\r","\r","    CommonCharacterLimits = [0 1000]\r","\r","    HighlightBackground (1,1) matlab.lang.OnOffSwitchState = \"off\"\r","    HighlightBackgroundColor (1,1) string = \"cyan\"\r","\r","  end  % properties\r","\r","  methods (Access=protected)\r","\r","    function setup(component)\r","      % uigridlayout creates 2-by-2 cells by default.\r","      % Specify [1 1] to create one cell.\r","      component.baseGridObject = uigridlayout(component, [1 1]);\r","\r","      % Create one row and one column.\r","      % Expand the cell to the outside element by specifying '1x'.\r","      % FYI, the opposite of '1x' is 'fit', which shrinks the grid cell.\r","      component.baseGridObject.RowHeight = {'1x'};\r","      component.baseGridObject.ColumnWidth = {'1x'};\r","\r","      % This base class sets spaces 0.\r","      % Override in the derived class if necessary. Don't change the values here.\r","      component.baseGridObject.Padding = [0 0 0 0];  % left bottom right top\r","      component.baseGridObject.ColumnSpacing = 0;\r","      component.baseGridObject.RowSpacing = 0;\r","\r","      component.HighlightBackground = \"off\";\r","\r","    end  % function\r","\r","    function update(component)\r","      if component.HighlightBackground\r","        component.baseGridObject.BackgroundColor = component.HighlightBackgroundColor;\r","      end  % if\r","    end  % function\r","\r","  end  % methods\r","\r","  properties (Access=protected, Transient, NonCopyable)\r","    baseGridObject matlab.ui.container.GridLayout\r","  end  % properties\r","\r","end  % classdef\r",""],"CoverageDisplayDataPerLine":{"Function":[{"LineNumber":38,"Hits":454,"StartColumnNumbers":4,"EndColumnNumbers":29,"ContinuedLine":false},{"LineNumber":59,"Hits":1130,"StartColumnNumbers":4,"EndColumnNumbers":30,"ContinuedLine":false}],"Statement":[{"LineNumber":41,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":64,"ContinuedLine":false},{"LineNumber":46,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":50,"ContinuedLine":false},{"LineNumber":47,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":52,"ContinuedLine":false},{"LineNumber":51,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":51,"ContinuedLine":false},{"LineNumber":52,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":49,"ContinuedLine":false},{"LineNumber":53,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":46,"ContinuedLine":false},{"LineNumber":55,"Hits":454,"StartColumnNumbers":6,"EndColumnNumbers":44,"ContinuedLine":false},{"LineNumber":60,"Hits":1130,"StartColumnNumbers":6,"EndColumnNumbers":38,"ContinuedLine":false},{"LineNumber":61,"Hits":0,"StartColumnNumbers":8,"EndColumnNumbers":86,"ContinuedLine":false}]}}